/* MODULE LED1. */

#include "LED1.h"

/*
** ===================================================================
**     Method      :  LED1_On (component LED)
**
**     Description :
**         This turns the LED on.
**     Parameters  : None
**     Returns     : Nothing
** ===================================================================
*/
void LED1_On(void)
{
  GPIO1_ClearPortBits(NULL, GPIO1_LED_PTE20_MASK);
}

/*
** ===================================================================
**     Method      :  LED1_Off (component LED)
**
**     Description :
**         This turns the LED off.
**     Parameters  : None
**     Returns     : Nothing
** ===================================================================
*/
void LED1_Off(void)
{
  GPIO1_SetPortBits(NULL, GPIO1_LED_PTE20_MASK);
}

/*
** ===================================================================
**     Method      :  LED1_Neg (component LED)
**
**     Description :
**         This negates/toggles the LED
**     Parameters  : None
**     Returns     : Nothing
** ===================================================================
*/
void LED1_Neg(void)
{
  GPIO1_TogglePortBits(NULL, GPIO1_LED_PTE20_MASK);
}

/*
** ===================================================================
**     Method      :  LED1_Get (component LED)
**
**     Description :
**         This returns logical 1 in case the LED is on, 0 otherwise.
**     Parameters  : None
**     Returns     :
**         ---             - Status of the LED (on or off)
** ===================================================================
*/
byte LED1_Get(void)
{
  return (byte)!((GPIO1_GetPortValue(NULL)&GPIO1_LED_PTE20_MASK)!=0);
}

/*
** ===================================================================
**     Method      :  LED1_Init (component LED)
**
**     Description :
**         Performs the LED driver initialization.
**     Parameters  : None
**     Returns     : Nothing
** ===================================================================
*/
void LED1_Init(void)
{
  LED1_Off();
}

/*
** ===================================================================
**     Method      :  LED1_Put (component LED)
**
**     Description :
**         Turns the LED on or off.
**     Parameters  :
**         NAME            - DESCRIPTION
**         val             - value to define if the LED has to be on or
**                           off.
**     Returns     : Nothing
** ===================================================================
*/
void LED1_Put(byte val)
{
  if(val) {
    LED1_On();
  } else {
    LED1_Off();
  }
}

